var mongoose = require('mongoose');
var db = mongoose.createConnection('mongodb://localhost:27017/node-android')
var Schema = mongoose.Schema;

var userSchema = mongoose.Schema({ 
	token : String,
	email: String, 
	hashed_password: String, 
	salt : String,
	temp_str:String
});

var estadoSchema = mongoose.Schema({
	animo: String,
	fecha: Date
});

var user = module.exports.user = db.model('users', userSchema);        
var estado = module.exports.estado = db.model('estados', estadoSchema);